import { Component } from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import { FormControl, FormGroup, Validators } from '@angular/forms';
import {Subscription} from "rxjs";
import {FotosService} from "../../services/fotos.service";
import {Foto} from "../../models/foto.model";

@Component({
  selector: 'app-fotos-detail-page',
  templateUrl: './fotos-detail-page.component.html',
  styleUrls: ['./fotos-detail-page.component.css']
})
export class FotosDetailPageComponent {
  foto: Foto = {id: -1, img: '', dsc: ''};
  private sub: any;

  form = new FormGroup({
    img: new FormControl(),
    dsc: new FormControl<string|null>(null, Validators.required)
  });

  constructor(
    private readonly route: ActivatedRoute,
    private fotos: FotosService
  ) {}

  ngOnInit() {
    this.sub = this.route.params.subscribe(params => {
      const id: number = +params['id']; // (+) converts string 'id' to a number
      this.foto = this.fotos.getFotoById(id);
    });
  }

  ngOnDestroy() {
    this.sub.unsubscribe();
  }

  onSubmit() {
    if(this.fotos.editPhotoById({id: 0, img: '', dsc: ''})) {
      this.router.navigate(['/main/fotos-details', image.id]);
    }
  }
}
<h1>{{foto.id}}</h1>
<img [src]="foto.img" alt="">
<p>{{foto.dsc}}</p>
<form (ngSubmit)="onSubmit()" [formGroup]="form">
    <h4>Edit:</h4>
    <p for="">Description:</p>
    <input type="text" formControlName="dsc" [ngModel]="foto.dsc"><br>
    <p for="">Image:</p>
    <input type="text" formControlName="img" [ngModel]="foto.img"><br>
    <button type="submit">Save</button>
</form>
